mapper.update:
update content set c_name=#{c_name}, c_level=#{c_level}, 
c_able=#{c_able}, c_price=#{c_price}, c_content=#{c_content}
where c_no=#{c_no}

mapper.select:
select sub_c_no, count(*) s_count, ROUND(SUM(sub_price),0) s_sales, c_name, if(c_level='1','초급', if(c_level='2','중급','고급')) level from subscribe s left join (select * from content) c on sub_c_no = c_no
where c_level = #{c_level}
group by c_level

select
CONCAT(if(cnt-30<=0,0,cnt-30),'%') c_discountrate, c_no, c_name, cnt, if(c_level=1,'초급', if(c_level=2,'중급', '고급')) c_level, (c_price*cnt/10) c_price, c_price*(cnt/10)*(1-(if(cnt-30<=0,0,cnt-30)/100)) c_sellprice
from cart c left join (select * from content) s on c.cr_c_no=s.c_no WHERE cr_m_no=#{cr_m_no}
