mapper.select:
	select s.* , content.c_name
	from subscribe s, content 
	where s.sub_c_no = content.c_no
    and t_m_id = #{t_m_id}


			and c_no like CONCAT('%', #{cri.searchWord}, '%')


    ORDER BY SUB_DATE LIMIT #{cri.startNo}, #{cri.amount}

select * from grp where t_m_id = #{t_m_id}


		and g_no like CONCAT('%', #{cri.searchWord}, '%')


		and g_name like CONCAT('%', #{cri.searchWord}, '%')


ORDER BY G_NO DESC LIMIT #{cri.startNo}, #{cri.amount}

select count(*) from subscribe
where t_m_id = #{t_m_id}


		and c_name like CONCAT('%', #{cri.searchWord}, '%')

select count(*) from grp where t_m_id = #{t_m_id}
and g_no like CONCAT('%', #{cri.searchWord}, '%')
and g_name like CONCAT('%', #{cri.searchWord}, '%')

	select count(*) from learner
	where l_g_no = #{g_no}
    and l_checkyn = 'Y'

	select count(*) from learner
	where l_g_no = #{g_no}
    and l_checkyn = 'N'
